mapper.select:
select u.*, (select d.name from sys_dept d where d.dept_id = u.dept_id) deptName from webchat_user u


		and u.`username` like concat('%',#{username},'%')

	and salt  IS NOT NULL

	${filterSql}



		order by u.${sidx} ${order}


		order by u.id desc



	limit #{offset}, #{limit}

select distinct rm.menu_id from webchat_user_role ur 
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id 
where ur.user_id = #{id}

mapper.delete:
delete from webchat_user where id in 

	#{id}

and salt  IS NOT NULL
;
delete from webchat_user_role where user_id in 

	#{id}
